package HomeTask;

public class Se
{

			 static int n=100;
			 static int s=1;
			
			public static void Number()
			{
				
				System.out.println("Decending order");
				for( int i=1;i<=n;i++)
				{
					System.out.println(i);
					
				}
				System.out.println("Ascending order");

				for( int i=100;i>=s;i--)
				{
					System.out.println(i);
					
				}
			}

			public static void main(String[] args)
			{
				// TODO Auto-generated method stub
				Number();

			}
}









